jQuery.fn.dataTableExt.oApi.fnGetColumnData=function(oSettings,iColumn,bUnique,bFiltered,bIgnoreEmpty){if(typeof iColumn=="undefined"){return[]}if(typeof bUnique=="undefined"){bUnique=true}if(typeof bFiltered=="undefined"){bFiltered=true}if(typeof bIgnoreEmpty=="undefined"){bIgnoreEmpty=true}var aiRows;if(bFiltered===true){aiRows=oSettings.aiDisplay}else{aiRows=oSettings.aiDisplayMaster}var asResultData=[];for(var i=0,c=aiRows.length;i<c;i++){var iRow=aiRows[i];var sValue=this.fnGetData(iRow,iColumn);if(bIgnoreEmpty===true&&sValue.length===0){continue}else if(bUnique===true&&jQuery.inArray(sValue,asResultData)>-1){continue}else{asResultData.push(sValue)}}return asResultData};